import React, { createContext, useState, useContext, useEffect } from 'react';
import { Moon, Sun, Orbit, MessageCircle, Users } from 'lucide-react';
import { Link } from 'react-router-dom';

// Theme Context
const ThemeContext = createContext({
  isDarkMode: false,
  toggleTheme: () => {}
});

// Theme Provider Component
export const ThemeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (savedTheme) {
      setIsDarkMode(savedTheme === 'dark');
    } else {
      setIsDarkMode(prefersDarkMode);
    }
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
    document.body.style.transition = 'background-color 0.5s, color 0.5s';
    document.body.style.backgroundColor = isDarkMode ? '#1a1a2e' : '#f3f4f6';
    document.body.style.color = isDarkMode ? '#ffffff' : '#1a1a2e';
  }, [isDarkMode]);

  const toggleTheme = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };

  return (
    <ThemeContext.Provider value={{ isDarkMode, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

// Custom hook to use the theme context
export const useTheme = () => useContext(ThemeContext);

// Feature Card Component
const FeatureCard = ({ icon, title, description }) => {
  const { isDarkMode } = useTheme();

  return (
    <div
      className={`p-6 rounded-lg shadow-lg transform transition-all duration-300 hover:-translate-y-2 hover:shadow-2xl 
      ${isDarkMode ? 'bg-gray-800 text-white' : 'bg-white text-black'}`}
    >
      <div className="mb-4 text-blue-500 text-4xl">{icon}</div>
      <h3 className="text-xl font-semibold mb-2">{title}</h3>
      <p className="text-sm opacity-70">{description}</p>
    </div>
  );
};

const HomePage = () => {
  const { isDarkMode, toggleTheme } = useTheme();

  return (
    <div
      className={`min-h-screen flex flex-col ${isDarkMode ? 'bg-dark-gradient' : 'bg-light-gradient'}`}
      style={{
        background: isDarkMode
          ? 'linear-gradient(to bottom, #1a1a2e, #16213e, #0f3460)'
          : 'linear-gradient(to bottom, #f3f4f6, #e2e8f0, #cbd5e1)'
      }}
    >
      {/* Navigation */}
      <nav className="relative z-10 flex justify-between items-center p-6 shadow-md">
        <div className="flex items-center space-x-4">
          <Orbit className="text-blue-500 animate-pulse" size={32} />
          <h1 className="text-2xl font-bold hover:text-blue-500 transition-all">ConnectSphere</h1>
        </div>

        <div className="flex items-center space-x-4">
          <button
            onClick={toggleTheme}
            className="p-2 rounded-full bg-gray-200 hover:bg-gray-300 dark:bg-gray-700 dark:hover:bg-gray-600 transition-all"
          >
            {isDarkMode ? <Sun className="text-yellow-500" /> : <Moon className="text-blue-500" />}
          </button>
          <Link
            to="/login"
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all"
          >
            Login
          </Link>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="container mx-auto px-6 py-12 text-center">
        <h2 className="text-5xl font-bold mb-6 text-shadow-lg">
          Discover Your Universe
        </h2>
        <p className="text-lg mb-12 max-w-2xl mx-auto">
          Connect, share, and grow through our AI-powered platform designed for meaningful connections.
        </p>

        <div className="grid md:grid-cols-3 gap-6 max-w-4xl mx-auto">
          <FeatureCard
            icon={<Orbit />}
            title="Find Your Orbit"
            description="Connect with like-minded campus peers"
          />
          <FeatureCard
            icon={<MessageCircle />}
            title="Cosmic Conversations"
            description="Secure, direct messaging platform"
          />
          <FeatureCard
            icon={<Users />}
            title="Share Your Universe"
            description="Post anonymous stories and experiences"
          />
        </div>

        <div className="mt-12">
          <Link
            to="/register"
            className="px-8 py-3 bg-blue-600 text-white rounded-full text-lg hover:bg-blue-700 transition-all"
          >
            Get Started
          </Link>
        </div>
      </div>

      {/* Footer */}
      <footer className="relative z-10 p-6 text-center text-sm opacity-70">
        <p className="hover:opacity-100 transition-opacity">
          © 2024 ConnectSphere. All rights reserved.
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
