import React, { useState } from 'react';
import { MessageCircle, Send } from 'lucide-react';
import { useTheme } from './ThemeContext';

const ConfessionPage: React.FC = () => {
  const { theme } = useTheme();
  const [confession, setConfession] = useState('');
  const [confessions, setConfessions] = useState([
    { 
      id: 1, 
      text: "I've always been afraid to share my true feelings, but this platform gives me hope.", 
      timestamp: new Date().toLocaleString() 
    }
  ]);

  const handleSubmitConfession = () => {
    if (confession.trim()) {
      setConfessions([
        ...confessions, 
        { 
          id: confessions.length + 1, 
          text: confession, 
          timestamp: new Date().toLocaleString() 
        }
      ]);
      setConfession('');
    }
  };

  return (
    <div className={`min-h-screen p-6 ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-white text-black'}`}>
      <div className="container mx-auto">
        <h1 className="text-3xl font-bold mb-6 flex items-center">
          <MessageCircle className="mr-3 text-green-500" /> Anonymous Confessions
        </h1>

        <div className="mb-6">
          <textarea
            placeholder="Share your story anonymously..."
            className="w-full p-3 rounded-lg border min-h-[150px]"
            value={confession}
            onChange={(e) => setConfession(e.target.value)}
          />
          <button 
            onClick={handleSubmitConfession}
            className="mt-2 w-full bg-green-500 text-white p-3 rounded-lg flex items-center justify-center"
          >
            <Send className="mr-2" /> Submit Confession
          </button>
        </div>

        <div className="space-y-4">
          {confessions.map(conf => (
            <div 
              key={conf.id} 
              className="bg-gray-100 p-4 rounded-lg shadow"
            >
              <p>{conf.text}</p>
              <small className="text-gray-500 block text-right mt-2">
                Posted: {conf.timestamp}
              </small>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ConfessionPage;
