import React, { createContext, useState, useContext, useEffect } from 'react';
import { Moon, Sun, Orbit, MessageCircle, Users, Lock } from 'lucide-react';

// Theme Context (kept from original implementation)
const ThemeContext = createContext({
  isDarkMode: false,
  toggleTheme: () => {}
});

// Theme Provider Component
export const ThemeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (savedTheme) {
      setIsDarkMode(savedTheme === 'dark');
    } else {
      setIsDarkMode(prefersDarkMode);
    }
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <ThemeContext.Provider value={{ isDarkMode, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

// Custom hook to use the theme context
export const useTheme = () => useContext(ThemeContext);

// HomePage Component
const HomePage = () => {
  const { isDarkMode, toggleTheme } = useTheme();

  return (
    <div className={`min-h-screen ${isDarkMode ? 'dark bg-gray-900 text-white' : 'bg-white text-black'} transition-colors duration-300 flex flex-col`}>
      {/* Navigation */}
      <nav className="flex justify-between items-center p-4 border-b dark:border-gray-700">
        <div className="flex items-center space-x-2">
          <Orbit color={isDarkMode ? 'white' : 'black'} size={32} />
          <h1 className="text-2xl font-bold">ConnectSphere</h1>
        </div>
        <div className="flex items-center space-x-4">
          <button 
            onClick={toggleTheme} 
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition"
          >
            {isDarkMode ? <Sun /> : <Moon />}
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <main className="flex-grow container mx-auto px-4 py-16 grid md:grid-cols-2 gap-12 items-center">
        <div className="space-y-6">
          <h2 className="text-5xl font-extrabold leading-tight dark:text-white">
            Find Your Perfect <span className="text-blue-600 dark:text-blue-400">Connection</span>
          </h2>
          <p className="text-xl text-gray-600 dark:text-gray-300">
            Discover meaningful connections through our AI-powered matching system. Share, connect, and grow together.
          </p>
          
          <div className="grid md:grid-cols-3 gap-4">
            <FeatureCard 
              icon={<Users />} 
              title="AI Matching" 
              description="Smart friend recommendations" 
            />
            <FeatureCard 
              icon={<MessageCircle />} 
              title="Direct Messaging" 
              description="Secure, private conversations" 
            />
            <FeatureCard 
              icon={<Lock />} 
              title="Anonymous" 
              description="Share freely, stay protected" 
            />
          </div>
        </div>
        
        <div className="hidden md:flex justify-center items-center">
          <div className="w-96 h-96 bg-blue-100 dark:bg-blue-900 rounded-full flex items-center justify-center">
            <Orbit 
              size={200} 
              color={isDarkMode ? '#3B82F6' : '#1E40AF'} 
              strokeWidth={1.5} 
            />
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gray-100 dark:bg-gray-800 py-8 mt-auto">
        <div className="container mx-auto px-4 text-center">
          <p className="text-gray-600 dark:text-gray-300">
            © 2024 ConnectSphere. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

// Feature Card Component
const FeatureCard = ({ icon, title, description }) => {
  const { isDarkMode } = useTheme();

  return (
    <div className={`p-4 rounded-lg border ${
      isDarkMode 
        ? 'bg-gray-800 border-gray-700 text-white' 
        : 'bg-white border-gray-200 text-black'
    } shadow-sm transition-all hover:shadow-md`}>
      <div className="mb-3 text-blue-600 dark:text-blue-400">
        {icon}
      </div>
      <h3 className="font-bold mb-2">{title}</h3>
      <p className="text-sm text-gray-600 dark:text-gray-300">{description}</p>
    </div>
  );
};

// App Component
const App = () => {
  return (
    <ThemeProvider>
      <HomePage />
    </ThemeProvider>
  );
};

export default App;
