import React, { useState } from 'react';
import { 
  MessageCircle, 
  Users, 
  Newspaper, 
  Moon, 
  Sun, 
  User, 
  Bell, 
  Settings, 
  LogOut 
} from 'lucide-react';
import { useTheme } from './ThemeContext';
import { useNavigate } from 'react-router-dom';

const ProfileDropdown = ({ isOpen, onClose }) => {
  const navigate = useNavigate();

  const menuItems = [
    { 
      icon: <User size={20} />, 
      text: 'Profile', 
      action: () => navigate('/profile') 
    },
    { 
      icon: <Settings size={20} />, 
      text: 'Settings', 
      action: () => navigate('/settings') 
    },
    { 
      icon: <LogOut size={20} className="text-red-500" />, 
      text: 'Logout', 
      action: () => {
        // Implement logout logic
        localStorage.removeItem('token');
        navigate('/login');
      } 
    }
  ];

  if (!isOpen) return null;

  return (
    <div className="absolute right-0 mt-2 w-56 bg-white dark:bg-gray-800 rounded-lg shadow-lg border dark:border-gray-700">
      <div className="py-1">
        {menuItems.map((item, index) => (
          <button
            key={index}
            onClick={item.action}
            className="flex items-center w-full px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
          >
            <span className="mr-3">{item.icon}</span>
            {item.text}
          </button>
        ))}
      </div>
    </div>
  );
};

const HomePage = () => {
  const [isLoggedIn] = useState(true); // Simulating logged-in state
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const { isDarkMode, toggleTheme } = useTheme();

  const features = [
    { 
      icon: <Users size={40} className="text-blue-500 dark:text-blue-300" />, 
      title: "Find Your Orbit", 
      description: "Connect with like-minded campus peers",
      color: "blue"
    },
    { 
      icon: <MessageCircle size={40} className="text-green-500 dark:text-green-300" />, 
      title: "Cosmic Conversations", 
      description: "Secure, direct messaging platform",
      color: "green"
    },
    { 
      icon: <Newspaper size={40} className="text-purple-500 dark:text-purple-300" />, 
      title: "Share Your Universe", 
      description: "Post anonymous stories and experiences",
      color: "purple"
    }
  ];

  return (
    <div className={`min-h-screen ${isDarkMode ? 'dark bg-gray-900 text-gray-100' : 'bg-gray-100 text-gray-900'}`}>
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-md p-4 flex justify-between items-center relative">
        <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">Orbit</div>
        
        <nav className="flex items-center space-x-4">
          {/* Notification Icon */}
          <button className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 relative">
            <Bell size={24} className="text-gray-600 dark:text-gray-300" />
            <span className="absolute top-0 right-0 h-2 w-2 bg-red-500 rounded-full"></span>
          </button>

          {/* Dark Mode Toggle */}
          <button 
            onClick={toggleTheme} 
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
          >
            {isDarkMode ? (
              <Sun size={24} className="text-yellow-500" />
            ) : (
              <Moon size={24} className="text-gray-700" />
            )}
          </button>

          {/* Profile Dropdown */}
          <div className="relative">
            <button 
              onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
              className="p-1 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              <User size={28} className="text-gray-600 dark:text-gray-300" />
            </button>
            <ProfileDropdown 
              isOpen={isProfileDropdownOpen} 
              onClose={() => setIsProfileDropdownOpen(false)} 
            />
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-8">
        <section className="text-center">
          <h1 className="text-4xl font-bold mb-4 text-gray-800 dark:text-gray-100">
            Welcome to Orbit
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 mb-8">
            Your gateway to meaningful campus connections. Discover, interact, and expand your social universe.
          </p>

          {/* Features Grid with Hover Effects */}
          <div className="grid md:grid-cols-3 gap-6 mt-12">
            {features.map((feature, index) => (
              <div 
                key={index} 
                className={`
                  bg-white dark:bg-gray-800 
                  p-6 rounded-lg shadow-md 
                  transition-all duration-300 
                  hover:shadow-xl 
                  hover:scale-105 
                  group
                  border border-transparent 
                  hover:border-${feature.color}-300
                `}
              >
                <div className="flex justify-center mb-4 transition-transform duration-300 group-hover:-translate-y-2">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2 dark:text-gray-100 
                  transition-colors duration-300 
                  group-hover:text-${feature.color}-600 
                  dark:group-hover:text-${feature.color}-400"
                >
                  {feature.title}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">
                  {feature.description}
                </p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-white dark:bg-gray-800 py-4 text-center">
        <p className="text-gray-600 dark:text-gray-400">
          © 2024 Orbit. Connecting Campuses, Creating Communities.
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
