import React from 'react';
import { Moon, Sun, Orbit, MessageCircle, Users, Lock } from 'lucide-react';
import { useTheme } from './ThemeContext';
import { Link } from 'react-router-dom';

// Feature Card Component
const FeatureCard = ({ icon, title, description }) => {
  const { isDarkMode } = useTheme();

  return (
    <div className={`p-6 rounded-lg shadow-md transition-all duration-300 
      ${isDarkMode 
        ? 'bg-gray-800 text-white hover:bg-gray-700' 
        : 'bg-white text-black hover:bg-gray-100'
      }`}>
      <div className="mb-4 text-blue-500">
        {icon}
      </div>
      <h3 className="text-xl font-semibold mb-2">{title}</h3>
      <p className="text-sm opacity-70">{description}</p>
    </div>
  );
};

const HomePage = () => {
  const { isDarkMode, toggleTheme } = useTheme();

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-black'}`}>
      {/* Navigation */}
      <nav className="flex justify-between items-center p-6">
        <div className="flex items-center space-x-4">
          <Orbit className="text-blue-500" size={32} />
          <h1 className="text-2xl font-bold">ConnectSphere</h1>
        </div>

        <div className="flex items-center space-x-4">
          <button onClick={toggleTheme} className="p-2 rounded-full">
            {isDarkMode ? <Sun className="text-yellow-500" /> : <Moon className="text-blue-500" />}
          </button>
          <Link to="/login" className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
            Login
          </Link>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="container mx-auto px-6 py-12 text-center">
        <h2 className="text-4xl font-bold mb-6">
          Find Your Perfect Connection
        </h2>

        <p className="text-xl mb-12 max-w-2xl mx-auto">
          Discover meaningful connections through our AI-powered matching system. 
          Share, connect, and grow together.
        </p>

        <div className="grid md:grid-cols-3 gap-6 max-w-4xl mx-auto">
          <FeatureCard 
            icon={<Orbit />} 
            title="Find Your Orbit" 
            description="Connect with like-minded campus peers" 
          />
          <FeatureCard 
            icon={<MessageCircle />} 
            title="Cosmic Conversations" 
            description="Secure, direct messaging platform" 
          />
          <FeatureCard 
            icon={<Users />} 
            title="Share Your Universe" 
            description="Post anonymous stories and experiences" 
          />
        </div>

        <div className="mt-12">
          <Link 
            to="/register" 
            className="px-8 py-3 bg-blue-600 text-white rounded-full text-lg hover:bg-blue-700 transition"
          >
            Get Started
          </Link>
        </div>
      </div>

      {/* Footer */}
      <footer className="p-6 text-center opacity-70">
        © 2024 ConnectSphere. All rights reserved.
      </footer>
    </div>
  );
};

export default HomePage;
