import React, { useState, useEffect } from 'react';
import { Home, MessageCircle, Users, Lock, Newspaper, Moon, Sun } from 'lucide-react';

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Check system preference and local storage on initial load
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;

    if (savedTheme) {
      setIsDarkMode(savedTheme === 'dark');
    } else {
      setIsDarkMode(prefersDarkMode);
    }
  }, []);

  // Update theme when dark mode changes
  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const features = [
    { 
      icon: <Users size={40} className="text-blue-500 dark:text-blue-300" />, 
      title: "Find Your Orbit", 
      description: "Connect with like-minded campus peers" 
    },
    { 
      icon: <MessageCircle size={40} className="text-green-500 dark:text-green-300" />, 
      title: "Cosmic Conversations", 
      description: "Secure, direct messaging platform" 
    },
    { 
      icon: <Newspaper size={40} className="text-purple-500 dark:text-purple-300" />, 
      title: "Share Your Universe", 
      description: "Post anonymous stories and experiences" 
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 dark:from-gray-900 dark:to-black text-gray-900 dark:text-gray-100 transition-colors duration-300 ease-in-out flex flex-col">
      {/* Header */}
      <header className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md shadow-md p-4 flex justify-between items-center">
        <div className="flex items-center">
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 50 50" 
            className="w-10 h-10 mr-3"
          >
            <circle cx="25" cy="25" r="20" fill="#3B82F6" />
            <circle cx="25" cy="25" r="15" fill="white" />
            <circle cx="25" cy="25" r="7" fill="#3B82F6" />
          </svg>
          <span className="text-2xl font-bold text-blue-600 dark:text-blue-400">Orbit</span>
        </div>
        <nav className="flex items-center space-x-4">
          {/* Dark Mode Toggle */}
          <button 
            onClick={toggleDarkMode}
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition"
            aria-label="Toggle Dark Mode"
          >
            {isDarkMode ? (
              <Sun size={24} className="text-yellow-500" />
            ) : (
              <Moon size={24} className="text-blue-600" />
            )}
          </button>

          {isLoggedIn ? (
            <button 
              className="bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600 transition"
              onClick={() => setIsLoggedIn(false)}
            >
              Logout
            </button>
          ) : (
            <button 
              className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition dark:bg-blue-600 dark:hover:bg-blue-700"
              onClick={() => setIsLoggedIn(true)}
            >
              Sign in/up
            </button>
          )}
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8 text-center">
        <section>
          <h1 className="text-5xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-600 dark:from-blue-400 dark:to-purple-400">
            Welcome to Orbit
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 mb-8 max-w-2xl mx-auto">
            Your gateway to meaningful campus connections. Discover, interact, and expand your social universe.
          </p>

          {/* Features Grid */}
          <div className="grid md:grid-cols-3 gap-6 mt-12">
            {features.map((feature, index) => (
              <div 
                key={index} 
                className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md p-6 rounded-lg shadow-lg hover:shadow-xl transition-all"
              >
                <div className="flex justify-center mb-4">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2 dark:text-gray-200">{feature.title}</h3>
                <p className="text-gray-600 dark:text-gray-400">{feature.description}</p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md py-4 text-center">
        <p className="text-gray-600 dark:text-gray-300">
          © 2024 Orbit. Connecting Campuses, Creating Communities.
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
