import React, { useState } from 'react';
import { Home, MessageCircle, Users, Lock, Newspaper } from 'lucide-react';

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const features = [
    { 
      icon: <Users size={40} className="text-blue-500" />, 
      title: "Find Your Orbit", 
      description: "Connect with like-minded campus peers" 
    },
    { 
      icon: <MessageCircle size={40} className="text-green-500" />, 
      title: "Cosmic Conversations", 
      description: "Secure, direct messaging platform" 
    },
    { 
      icon: <Newspaper size={40} className="text-purple-500" />, 
      title: "Share Your Universe", 
      description: "Post anonymous stories and experiences" 
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 flex flex-col">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md shadow-md p-4 flex justify-between items-center">
        <div className="flex items-center">
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 50 50" 
            className="w-10 h-10 mr-3"
          >
            <circle cx="25" cy="25" r="20" fill="#3B82F6" />
            <circle cx="25" cy="25" r="15" fill="white" />
            <circle cx="25" cy="25" r="7" fill="#3B82F6" />
          </svg>
          <span className="text-2xl font-bold text-blue-600">Orbit</span>
        </div>
        <nav>
          {isLoggedIn ? (
            <button 
              className="bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600 transition"
              onClick={() => setIsLoggedIn(false)}
            >
              Logout
            </button>
          ) : (
            <button 
              className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition"
              onClick={() => setIsLoggedIn(true)}
            >
              Sign in/up
            </button>
          )}
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8 text-center">
        <section>
          <h1 className="text-5xl font-bold mb-4 text-gray-800 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-600">
            Welcome to Orbit
          </h1>
          <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
            Your gateway to meaningful campus connections. Discover, interact, and expand your social universe.
          </p>

          {/* Features Grid */}
          <div className="grid md:grid-cols-3 gap-6 mt-12">
            {features.map((feature, index) => (
              <div 
                key={index} 
                className="bg-white/80 backdrop-blur-md p-6 rounded-lg shadow-lg hover:shadow-xl transition-all"
              >
                <div className="flex justify-center mb-4">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                <p className="text-gray-600">{feature.description}</p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-white/80 backdrop-blur-md py-4 text-center">
        <p className="text-gray-600">
          Â© 2024 Orbit. Connecting Campuses, Creating Communities.
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
