import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Upload, User, Lock, Mail, BookOpen } from 'lucide-react';

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    fullName: '',
    username: '',
    collegeRollNo: '',
    bio: '',
    profilePic: null
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    
    if (name === 'profilePic' && files[0]) {
      setFormData(prev => ({
        ...prev,
        profilePic: files[0]
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email: formData.email,
        password: formData.password
      });

      // Save token to localStorage
      localStorage.setItem('token', response.data.token);
      
      // Redirect to home page
      navigate('/');
    } catch (err) {
      setError('Invalid credentials. Please try again.');
      console.error(err);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    
    try {
      // Create FormData for file upload
      const registrationData = new FormData();
      Object.keys(formData).forEach(key => {
        if (formData[key] !== null) {
          registrationData.append(key, formData[key]);
        }
      });

      const response = await axios.post('http://localhost:5000/api/auth/register', registrationData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Save token to localStorage
      localStorage.setItem('token', response.data.token);
      
      // Redirect to home page
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-3xl font-bold mb-6 text-center flex items-center justify-center">
          <User className="mr-2 text-blue-500" /> 
          {isLogin ? 'Welcome Back' : 'Create Your Profile'}
        </h2>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        
        <form onSubmit={isLogin ? handleLogin : handleRegister} className="space-y-4">
          {/* Email and Password (Always Shown) */}
          <div>
            <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">
              College Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="your.email@college.edu"
              required
            />
          </div>

          <div>
            <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="********"
              required
            />
          </div>

          {/* Additional Registration Fields */}
          {!isLogin && (
            <>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Profile Picture
                </label>
                <div className="flex items-center">
                  <input
                    type="file"
                    name="profilePic"
                    accept="image/*"
                    onChange={handleChange}
                    className="hidden"
                    id="profilePic"
                  />
                  <label 
                    htmlFor="profilePic" 
                    className="flex items-center px-4 py-2 bg-blue-500 text-white rounded-lg cursor-pointer hover:bg-blue-600"
                  >
                    <Upload className="mr-2" size={20} />
                    Upload Picture
                  </label>
                  {formData.profilePic && (
                    <span className="ml-2 text-sm">{formData.profilePic.name}</span>
                  )}
                </div>
              </div>

              <div>
                <label htmlFor="fullName" className="block text-gray-700 text-sm font-bold mb-2">
                  Full Name
                </label>
                <input
                  type="text"
                  id="fullName"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="John Doe"
                  required
                />
              </div>

              <div>
                <label htmlFor="username" className="block text-gray-700 text-sm font-bold mb-2">
                  Username
                </label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="johndoe123"
                  required
                />
              </div>

              <div>
                <label htmlFor="collegeRollNo" className="block text-gray-700 text-sm font-bold mb-2">
                  College Roll Number
                </label>
                <input
                  type="text"
                  id="collegeRollNo"
                  name="collegeRollNo"
                  value={formData.collegeRollNo}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="2022CSXXXX"
                  required
                />
              </div>

              <div>
                <label htmlFor="bio" className="block text-gray-700 text-sm font-bold mb-2">
                  Bio
                </label>
                <textarea
                  id="bio"
                  name="bio"
                  value={formData.bio}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Tell us a bit about yourself..."
                  rows="4"
                />
              </div>
            </>
          )}

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition"
          >
            {isLogin ? 'Log In' : 'Create Account'}
          </button>
        </form>

        <div className="mt-4 text-center">
          <p className="text-sm">
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              onClick={() => setIsLogin(!isLogin)}
              className="text-blue-500 hover:underline"
            >
              {isLogin ? 'Register' : 'Log In'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
