import React from 'react';
import { ThemeProvider, useTheme } from './ThemeContext';
import { Orbit } from 'lucide-react';

const WelcomeSection = () => {
  const { isDarkMode, ThemeToggler } = useTheme();
  
  return (
    <div className={`min-h-screen flex flex-col justify-center items-center 
      ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-black'}
      transition-colors duration-300 ease-in-out`}
    >
      <ThemeToggler />
      
      <div className="text-center max-w-2xl px-6">
        <div className="flex justify-center mb-8">
          <Orbit 
            className={`w-24 h-24 ${
              isDarkMode 
                ? 'text-blue-400 animate-pulse' 
                : 'text-blue-600 animate-spin'
            }`} 
          />
        </div>
        
        <h1 className={`
          text-4xl md:text-5xl font-bold mb-6 
          ${isDarkMode 
            ? 'text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-600' 
            : 'text-gray-800'
          }`}
        >
          Welcome to Orbit
        </h1>
        
        <p className={`
          text-xl leading-relaxed mb-10
          ${isDarkMode 
            ? 'text-gray-300' 
            : 'text-gray-600'
          }`}
        >
          Your journey, your peers, your stories.
          Let’s make your campus connections timeless.
        </p>
        
        <div className="flex justify-center space-x-4">
          <button className={`
            px-6 py-3 rounded-full font-semibold transition-all
            ${isDarkMode 
              ? 'bg-blue-600 text-white hover:bg-blue-700 ' 
              : 'bg-blue-500 text-white hover:bg-blue-600 shadow-md'
            }`}
          >
            Get Started
          </button>
          <button className={`
            px-6 py-3 rounded-full font-semibold border-2 transition-all
            ${isDarkMode 
              ? 'border-blue-400 text-blue-400 hover:bg-blue-900/30' 
              : 'border-blue-500 text-blue-500 hover:bg-blue-50'
            }`}
          >
            Learn More
          </button>
        </div>
      </div>
      
      <footer className={`
        absolute bottom-4 text-center w-full
        ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}
      `}>
        © {new Date().getFullYear()} Your Company. All rights reserved.
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <ThemeProvider>
      <WelcomeSection />
    </ThemeProvider>
  );
};

export default App;
