import React, { useState } from 'react';
import { Home, MessageCircle, Users, Lock, Newspaper, Moon, Sun } from 'lucide-react';
import { useTheme } from './ThemeContext';

const ThemeToggle = () => {
  const { isDarkMode, toggleTheme } = useTheme();

  return (
    <button 
      onClick={toggleTheme} 
      className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors"
    >
      {isDarkMode ? (
        <Sun className="text-yellow-500" size={24} />
      ) : (
        <Moon className="text-indigo-600" size={24} />
      )}
    </button>
  );
};

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const { isDarkMode } = useTheme();

  const features = [
    { 
      icon: <Users size={40} className="text-blue-500 dark:text-blue-400" />, 
      title: "Find Your Orbit", 
      description: "Connect with like-minded campus peers" 
    },
    { 
      icon: <MessageCircle size={40} className="text-green-500 dark:text-green-400" />, 
      title: "Cosmic Conversations", 
      description: "Secure, direct messaging platform" 
    },
    { 
      icon: <Newspaper size={40} className="text-purple-500 dark:text-purple-400" />, 
      title: "Share Your Universe", 
      description: "Post anonymous stories and experiences" 
    }
  ];

  return (
    <div className={`
      min-h-screen 
      ${isDarkMode 
        ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-black text-gray-100' 
        : 'bg-gradient-to-br from-blue-50 to-purple-100 text-gray-900'
      } 
      flex flex-col transition-colors duration-300 ease-in-out
    `}>
      {/* Header */}
      <header className={`
        ${isDarkMode 
          ? 'bg-gray-800/80 text-gray-100' 
          : 'bg-white/80 text-gray-900'
        } 
        backdrop-blur-md shadow-md p-4 flex justify-between items-center
      `}>
        <div className="flex items-center">
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 50 50" 
            className="w-10 h-10 mr-3"
          >
            <circle 
              cx="25" 
              cy="25" 
              r="20" 
              fill={isDarkMode ? '#4A5568' : '#3B82F6'} 
            />
            <circle 
              cx="25" 
              cy="25" 
              r="15" 
              fill={isDarkMode ? '#1A202C' : 'white'} 
            />
            <circle 
              cx="25" 
              cy="25" 
              r="7" 
              fill={isDarkMode ? '#4A5568' : '#3B82F6'} 
            />
          </svg>
          <span className={`
            text-2xl font-bold 
            ${isDarkMode ? 'text-gray-100' : 'text-blue-600'}
          `}>
            Orbit
          </span>
        </div>
        <nav className="flex items-center space-x-4">
          <ThemeToggle />
          {isLoggedIn ? (
            <button 
              className={`
                ${isDarkMode 
                  ? 'bg-red-700 hover:bg-red-600 text-white' 
                  : 'bg-red-500 hover:bg-red-600 text-white'}
                px-4 py-2 rounded-full transition
              `}
              onClick={() => setIsLoggedIn(false)}
            >
              Logout
            </button>
          ) : (
            <button 
              className={`
                ${isDarkMode 
                  ? 'bg-blue-700 hover:bg-blue-600 text-white' 
                  : 'bg-blue-500 hover:bg-blue-600 text-white'}
                px-4 py-2 rounded-full transition
              `}
              onClick={() => setIsLoggedIn(true)}
            >
              Sign in/up
            </button>
          )}
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8 text-center">
        <section>
          <h1 className={`
            text-5xl font-bold mb-4 
            ${isDarkMode 
              ? 'text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400' 
              : 'text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600'
            }
          `}>
            Welcome to Orbit
          </h1>
          <p className={`
            text-xl mb-8 max-w-2xl mx-auto 
            ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}
          `}>
            Your gateway to meaningful campus connections. Discover, interact, and expand your social universe.
          </p>

          {/* Features Grid */}
          <div className="grid md:grid-cols-3 gap-6 mt-12">
            {features.map((feature, index) => (
              <div 
                key={index} 
                className={`
                  ${isDarkMode 
                    ? 'bg-gray-800/80 text-gray-200 hover:bg-gray-700/80' 
                    : 'bg-white/80 text-gray-800 hover:bg-white'}
                  backdrop-blur-md p-6 rounded-lg shadow-lg hover:shadow-xl transition-all
                `}
              >
                <div className="flex justify-center mb-4">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                <p className={`
                  ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}
                `}>
                  {feature.description}
                </p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className={`
        ${isDarkMode 
          ? 'bg-gray-800/80 text-gray-300' 
          : 'bg-white/80 text-gray-600'}
        backdrop-blur-md py-4 text-center
      `}>
        <p>
          © 2024 Orbit. Connecting Campuses, Creating Communities.
        </p>
      </footer>
    </div>
  );
};

export default HomePage;
